package org.sgk.controller;import java.util.Date;import java.util.Locale;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@Controller@RequestMapping("/welcome")public class WelcomeController {	@RequestMapping(method = RequestMethod.GET)	public String welcome(Locale locale, Model model) {		System.out.println("current locale=" + locale);		Date date = new Date();		model.addAttribute("today", date);		return "welcome";	}}